import React from "react";

class Stacks extends React.Component {
	constructor() {
		super();
		this.state = {
			frontEnd: {
				react: true,
				angular: false,
				vue: false
			},
            backEnd: {
				react: true,
				angular: false,
				vue: false
			},
		};
	}

	handleClick = (event) => {
		const { name, checked } = event.target;

		this.setState((prevState) => {
			const frontEnd = prevState.frontEnd;
			frontEnd[name] = checked;
			return frontEnd;
		});
	};

	render() {
		const selectedStack = Object.keys(this.state.frontEnd)
			.filter((key) => this.state.frontEnd[key])
			.join(", ");
		return (
			<div className="stacks">
				<main>
					<div>
						<div>
							<input checked={this.state.frontEnd.react} onChange={this.handleClick} type="checkbox" name="react" /> React
						</div>
						<div>
							<input checked={this.state.frontEnd.angular} onChange={this.handleClick} type="checkbox" name="angular" /> Angular
						</div>
						<div>
							<input checked={this.state.frontEnd.vue} onChange={this.handleClick} type="checkbox" name="vue" /> Vue
						</div>
						
					</div>
					<p> Your favourite colors are: {selectedStack}</p>
				</main>
			</div>
		);
	}
}

export default Stacks;
