import React from "react";
import Stacks from "./stacks";
import Contact from "./Contact";

function Home(){

    const [name, setName] = useState(null);
    let appName;
    const onChange = e => {
    appName = e.target.value;
  }
    return(
        <div className="home-template">
        <section class="section section-1 w-100">
            <div class="container">
                    <div class="row">
                        <div class="col-sm-12 px-4 mx-4">
                        <h3 class="title position-relative">Letâ€™s build something <span>cool</span>!</h3>
                        <p>There are only 3 steps in the process: tech stack, starter template and database schema.</p>
                        </div>
                    </div>
            </div>
        </section>

        <section class="section section-2 w-100">
            <div class="container">
                    <div class="row">
                        <div class="col-sm-12 px-4 mx-4">
                            <h5 class="title position-relative">Application Name</h5>
                            <label>Name:</label><br />
                            <input onChange={onChange} type='text' placeholder="Enter application name"/>
                            <button onClick={() => setName(appName)}>set</button>
                            name: {name}
                        </div>
                    </div>
            </div>
        </section>

        <section class="section section-3 w-100">
            <div class="container">
                    <div class="row step">
                        <div class="col-sm-12 px-4 mx-4">
                        <h5 class="title position-relative">Stack</h5>
                        <Stacks />
                        </div>
                    </div>
            </div>
        </section>
        </div>
    )
};

export default Home;
