import React from "react";

class Stacks extends React.Component {
	constructor() {
		super();
		this.state = {
			frontEnd: {
				react: true,
				angular: false,
				vue: false
			},
            backEnd: {
				noBackend: true,
				nodejsTypeORM: false,
				nodejsSequeslize: false
			},
            dataBase: {
                postgreSQL: true,
                mySQL: false
            }
		};
	}

	handleClick = (event) => {
		const { name, checked } = event.target;

		this.setState((prevState) => {
			const frontEnd = prevState.frontEnd;
			frontEnd[name] = checked;
			return frontEnd;

            const backEnd = prevState.backEnd;
			backEnd[name] = checked;
			return backEnd;

            const dataBase = prevState.dataBase;
			dataBase[name] = checked;
			return dataBase;
		});
	};

	render() {
		const selectedFrontEndStack = Object.keys(this.state.frontEnd)
			.filter((key) => this.state.frontEnd[key])
			.join(", ");
        
        const selectedBackEndStack = Object.keys(this.state.backEnd)
			.filter((key) => this.state.backEnd[key])
			.join(", ");

        const selectedDataBaseStack = Object.keys(this.state.dataBase)
			.filter((key) => this.state.dataBase[key])
			.join(", ");
		return (
			<div className="stacks">
				<main>
					<div>
						<div>
							<input checked={this.state.frontEnd.react} onChange={this.handleClick} type="checkbox" name="react" /> 
                            <h5>React</h5>
                            <p>React is a frontend library used to create stateful and reusable UI components for developing large web applications.</p>
						</div>
						<div>
							<input checked={this.state.frontEnd.angular} onChange={this.handleClick} type="checkbox" name="angular" /> 
                            <h5>Angular</h5>
                            <p>Angular quickly established itself as a mainstream technology used by millions of developers.</p>
						</div>
						<div>
							<input checked={this.state.frontEnd.vue} onChange={this.handleClick} type="checkbox" name="vue" /> 
                            <h5>Vue</h5>
                            <p>Vue.js is a progressive framework for building user interfaces. Vue was built to be incrementally adoptable for users.</p>
						</div>
						
                        <div>
							<input checked={this.state.backEnd.noBackend} onChange={this.handleClick} type="checkbox" name="noBackend" /> 
                            <h5>No Backend</h5>
                            <p>Application without backend and database, only with front-end. 'create-react-app' for example.</p>
						</div>

                        <div>
							<input checked={this.state.dataBase.postgreSQL} onChange={this.handleClick} type="checkbox" name="postgreSQL" /> 
                            <h5>PostgreSQL</h5>
                            <p>Application without backend and database, only with front-end. 'create-react-app' for example.</p>
						</div>

                        <div>
							<input checked={this.state.dataBase.mySQL} onChange={this.handleClick} type="checkbox" name="mySQL" /> 
                            <h5>MySQL</h5>
                            <p>Application without backend and database, only with front-end. 'create-react-app' for example.</p>
						</div>


					</div>
					<p> Your favourite colors are: {selectedFrontEndStack}, {selectedBackEndStack}, {selectedDataBaseStack}</p>
				</main>
			</div>
		);
	}
}

export default Stacks;
